class Spell:
    def __init__(self, name, mana_cost):
        self.name = name
        self.mana_cost = mana_cost

    def cast(self):
        return f'Casting {self.name} (Cost: {self.mana_cost} mana)'

class FireSpell(Spell):
    def __init__(self, name, mana_cost, burn_damage):
        super().__init__(name, mana_cost)
        self.burn_damage = burn_damage

    def cast(self):
        base = super().cast()
        return f'{base} - Burns for {self.burn_damage} damage!'

class IceSpell(Spell):
    def __init__(self, name, mana_cost, freeze_duration):
        super().__init__(name, mana_cost)
        self.freeze_duration = freeze_duration

    def cast(self):
        base = super().cast()
        return f'{base} - Freezes for {self.freeze_duration} seconds!'

