class ProphecyError(Exception):
    """Base exception for prophecy-related errors"""
    pass

class CrystalCloudyError(ProphecyError):
    """Raised when the crystal ball is too cloudy to read"""
    pass

def divine_prophecy(crystal_clarity, question_complexity):
    try:
        if crystal_clarity < 0.3:
            raise CrystalCloudyError('Crystal ball is too cloudy for divination')

        if question_complexity > 0.8:
            raise ProphecyError('Question is too complex for mortal comprehension')

        # Simulate prophecy generation
        prophecy_power = crystal_clarity * (1 - question_complexity)

        if prophecy_power > 0.5:
            return f'Clear prophecy: Your future shines bright with {prophecy_power:.2f} clarity!'
        else:
            return f'Vague prophecy: The signs are unclear, only {prophecy_power:.2f} clarity achieved.'

    except CrystalCloudyError as e:
        return f'Crystal Error: {e}'
    except ProphecyError as e:
        return f'Prophecy Error: {e}'
    except Exception as e:
        return f'Unexpected magical interference: {e}'
    finally:
        print('Prophecy session concluded. Crystal ball cooling down.')

# Test different scenarios
print(divine_prophecy(0.8, 0.3))  # Clear prophecy
print(divine_prophecy(0.2, 0.5))  # Cloudy crystal
