def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n - 1)

result = factorial(4)
print(result)

def check_guess(guess, secret=7):
    if guess == secret:
        return 'You won!'
    elif guess > secret:
        return 'Too high!'
    else:
        return 'Too low!'

result = check_guess(7)
print(result)

class Character:
    def __init__(self, name, health, level):
        self.name = name
        self.health = health
        self.level = level

class Mage(Character):
    def cast_spell(self):
        print(f'{self.name} casts a powerful spell!')

gandalf = Mage('Gandalf', 80, 10)
gandalf.cast_spell()

def divide_safely(a, b):
    try:
        return a / b
    except ZeroDivisionError:
        return 'Cannot divide by zero!'

result = divide_safely(10, 0)
print(result)

def magical_effect(func):
    def wrapper():
        print('Magical effect activated!')
        return func()
    return wrapper

@magical_effect
def cast_fireball():
    print('Fireball launched!')

cast_fireball()

class QuestManager:
    def __init__(self):
        self.quests = []
        self.completed = []

    def add_quest(self, quest):
        self.quests.append(quest)

    def complete_quest(self, quest):
        if quest in self.quests:
            self.quests.remove(quest)
            self.completed.append(quest)

    def get_active_quests(self):
        return self.quests

qm = QuestManager()
qm.add_quest('Defeat Dragon')
qm.complete_quest('Defeat Dragon')
print('Quest system ready!')

def check_password_strength(password):
    if len(password) < 8:
        return 'Weak'

    has_upper = any(c.isupper() for c in password)
    has_lower = any(c.islower() for c in password)
    has_digit = any(c.isdigit() for c in password)

    if has_upper and has_lower and has_digit:
        return 'Strong'
    elif (has_upper and has_lower) or (has_upper and has_digit) or (has_lower and has_digit):
        return 'Medium'
    else:
        return 'Weak'

passwords = ['hello', 'Hello123', 'HELLO123', 'HelloWorld123']
for pwd in passwords:
    print(f'{pwd}: {check_password_strength(pwd)}')

