class Wizard:
    def __init__(self, name, mana):
        self.name = name
        self.mana = mana

    def cast_spell(self, spell_name):
        if self.mana >= 10:
            self.mana -= 10
            print(f'{self.name} casts {spell_name}! Mana: {self.mana}')
        else:
            print(f'{self.name} is out of mana!')

wizard = Wizard('Gandalf', 50)
wizard.cast_spell('Fireball')
wizard.cast_spell('Lightning')
wizard.cast_spell('Heal')

class Fighter:
    def __init__(self, name, health, attack):
        self.name = name
        self.health = health
        self.attack = attack

    def attack_enemy(self, enemy):
        enemy.health -= self.attack
        print(f'{self.name} attacks {enemy.name} for {self.attack} damage!')
        print(f'{enemy.name} health: {enemy.health}')

fighter1 = Fighter('Hero', 100, 25)
fighter2 = Fighter('Goblin', 80, 20)

while fighter1.health > 0 and fighter2.health > 0:
    fighter1.attack_enemy(fighter2)
    if fighter2.health <= 0:
        print(f'{fighter1.name} wins!')
        break
    fighter2.attack_enemy(fighter1)
    if fighter1.health <= 0:
        print(f'{fighter2.name} wins!')
        break

def safe_divide(a, b):
    try:
        result = a / b
        return result
    except ZeroDivisionError:
        print('Error: Cannot divide by zero!')
        return None
    except ValueError:
        print('Error: Please enter valid numbers!')
        return None

while True:
