        print('Please enter valid numbers!')

import time

def timing_decorator(func):
    def wrapper(*args, **kwargs):
        start_time = time.time()
        result = func(*args, **kwargs)
        end_time = time.time()
        print(f'{func.__name__} took {end_time - start_time:.4f} seconds')
        return result
    return wrapper

@timing_decorator
def fibonacci(n):
    if n <= 1:
        return n
    return fibonacci(n-1) + fibonacci(n-2)

result = fibonacci(10)
print(f'Fibonacci(10) = {result}')

import random

class Player:
    def __init__(self, name):
        self.name = name
        self.health = 100
        self.mana = 50
        self.attack = 20
        self.inventory = ['Health Potion', 'Mana Potion']

    def use_item(self, item):
        if item in self.inventory:
            if item == 'Health Potion':
                self.health = min(100, self.health + 30)
                print(f'{self.name} uses Health Potion! Health: {self.health}')
            elif item == 'Mana Potion':
                self.mana = min(50, self.mana + 20)
                print(f'{self.name} uses Mana Potion! Mana: {self.mana}')
            self.inventory.remove(item)
        else:
            print(f'You don\'t have {item}')

class Enemy:
    def __init__(self, name, health, attack):
        self.name = name
        self.health = health
        self.attack = attack

def battle(player, enemy):
    print(f'Battle begins! {player.name} vs {enemy.name}')

    while player.health > 0 and enemy.health > 0:
        # Player turn
        action = input('Choose action (attack/item): ').lower()

        if action == 'attack':
            damage = random.randint(15, 25)
            enemy.health -= damage
            print(f'{player.name} attacks for {damage} damage!')
            print(f'{enemy.name} health: {enemy.health}')
        elif action == 'item':
            if player.inventory:
                item = player.inventory[0]
                player.use_item(item)
            else:
                print('No items available!')

        if enemy.health <= 0:
            print(f'{player.name} wins!')
            break

        # Enemy turn
        damage = random.randint(10, 20)
        player.health -= damage
        print(f'{enemy.name} attacks for {damage} damage!')
        print(f'{player.name} health: {player.health}')

        if player.health <= 0:
            print(f'{enemy.name} wins!')
            break

# Game starts
player = Player('Hero')
enemy = Enemy('Orc', 80, 15)
battle(player, enemy)
